import consumer from "./consumer"

// document.addEventListener('turbolinks:load', () => {

    consumer.subscriptions.create("CommentChannel", {
      connected() {
        // Called when the subscription is ready for use on the server
        // console.log("Connected to server! Congrats!!!")
      },

      disconnected() {
        // Called when the subscription has been terminated by the server
        // console.log("DISCONNECTED !!!")
      },

      received(data) {
        // Called when there's incoming data on the websocket for this channel
alert(data['updated_at']);

        const comment_body = document.getElementById(`comment_body_${data['id']}`);
        comment_body.innerText = data['body'];
        const badge = `    <span class="badge  badge-warning">JUST EDITED (at: ${data['updated_at']})</span>`;
        const element = document.getElementById(`comment-updated-badge-${data['id]']}`);
        element.innerHTML = badge;
alert(JSON.stringify(element));

      }
    });

// })
