import consumer from "./consumer"

consumer.subscriptions.create("CommentChannel", {
  connected() {
    // Called when the subscription is ready for use on the server
    console.log("Connected to server! Congrats!!!")
  },

  disconnected() {
    // Called when the subscription has been terminated by the server
    console.log("DISCONNECTED !!!")
  },

  received(data) {
    // Called when there's incoming data on the websocket for this channel
    alert(data['comment_id'] + data['comment_body']);
    console.log(data['comment_id'], "===", data['comment_body'])

    const comment_body = document.getElementById(`comment_body_${data[comment_id]}`);
    comment_body.innerText = data['comment_body']

